    private void jButton2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton2MouseClicked
        String msg1 = "Password Mismatch";
        String msg2 = "Please set the password contains at least 8 characters including at least 1 digit,1 lowercase alpha and 1 uppercase alpha,1 special characters!";
        String msg3 = "New Admin " + jTextField1.getText() + " Registered Successfully!";
        if (!(String.valueOf(jPasswordField2.getPassword()).equals(String.valueOf(jPasswordField1.getPassword())))) {
            JOptionPane.showMessageDialog(this, msg1);
        } else if (!String.valueOf(jPasswordField1.getPassword()).matches("^.*(?=.{8,})(?=..*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&+=]).*$")) {
            JOptionPane.showMessageDialog(this, msg2);
        } else {
            
            JOptionPane.showMessageDialog(this, msg3);
        }
    }//GEN-LAST:event_jButton2MouseClicked

